import axios from "axios"
import React, {useState, useEffect} from "react"
import {useStateValue} from "../../StateProvider"
import "./ProductDetails.css"
import {withRouter, useParams} from "react-router"

const ProductDetails = () => {
  const {ProductId} = useParams()
  const [Product, setProduct] = useState({})
  const [Products, setProducts] = useState([])
  const [{basket}, dispatch] = useStateValue()

  function checkForId() {
    return
  }

  useEffect(() => {
    function componentDidMount() {
      return axios
        .get("/Product/getall")
        .then((response) => {
          setProducts(response.data.data)
        })
        .catch((err) => console.log(err))
    }
    componentDidMount()
    console.log(Products)
    Products.filter((aProduc) => aProduc.productId == ProductId).map(
      (filteredProduc) => {
        setProduct(filteredProduc)
      }
    )
  })

  const {
    productId,
    categoryId,
    productName,
    description,
    quantity,
    imageUrl,
    rating,
    comments,
    price,
  } = Product

  let ProductName = productName
  let CategoryId = categoryId
  let Description = description
  let Quantity = quantity
  let ImageUrl = imageUrl
  let Rating = rating
  let Comments = comments
  let Price = price

  let Rating2 = Number(Rating)

  const addToBasket = () => {
    // dispatch the item into the data layer
    dispatch({
      type: "ADD_TO_BASKET",
      item: {
        ProductId: ProductId,
        ProductName: ProductName,
        ImageUrl: ImageUrl,
        Price: Price,
        Rating: Rating,
      },
    })
  }

  return (
    <div className="details__container">
      <div>
        <img
          className="details__homeimage"
          src="https://coolbackgrounds.io/images/backgrounds/index/ranger-4df6c1b6.png"
          alt=""
        />
      </div>
      <div className="details__content">
        <div className="imageandinfo">
          <div className="details__image">
            <img className="image" src={ImageUrl} alt="product image" />
          </div>
          <div className="details__info">
            <div className="details__title">
              <h1 style={{color: "#001f3f"}}>{ProductName}</h1>
            </div>
            <ul>
              <li>
                <h3> Description : {Description}</h3>
              </li>

              <li>
                <h3> Quantity : {Quantity}</h3>
              </li>

              <li>
                <h3> Rating : {Rating}</h3>
              </li>
              <li>
                <h3> ProductID : {ProductId}</h3>
              </li>

              <li>
                <h3> CategoryID : {CategoryId}</h3>
              </li>

              <li>
                <h3> Price : {Price}</h3>
              </li>

              <li>
                <h3> Comments : {Price}</h3>
              </li>

              <div className="product__rating">
                <p>
                  {Array(Rating)
                    .fill()
                    .map((star, index) => {
                      return <span key={index}>ðŸŒŸ</span>
                    })}
                </p>
              </div>
            </ul>
            <button className="btn" onClick={addToBasket}>
              Add to Basket
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default ProductDetails

